package builderPattern;

public class Direcotr {

	//持有当前需要使用的建造器对象
	private Builder builder;
	
	/**
	 * 构造方法，传入建造器对象
	 */
	public Direcotr(Builder builder) {
		// TODO Auto-generated constructor stub
		this.builder=builder;
	}
	
	/**
	 * 产品构造方法，负责调用各个零件建造方法
	 */
	public void construct(){
		builder.buildPart1();
		builder.buildPart2();
	}
}
